def get_detailed_info(obs):
     """
     Gets detailed info for the observation
     """
     title = obs['identifier']['title'].title()
     for key in detailed_info:
         if (title == key):
             #obs['detailed_info'] = detailed_info[key]
             info = detailed_info[key]
             obs['detailed_info'] = {
                 'description': info['description'],
                 'commentary': info['commentary'][0] + obs['priority'][1].lower() + "."}
             if obs['priority'][0] in info['recommendation']:                 
                 obs['recommendation'] = info['recommendation'][obs['priority'][0]]